mapper.select:
SELECT hr.id,hr.healthUser,u.userName,hr.healthDescription,hr.reportDate,hr.bodyTemperature,hr.rnaTest,
        (CASE WHEN hr.needQuarantine = 1 THEN '需要' WHEN hr.needQuarantine = 0 THEN '不需要' ELSE '数据错误' END) needQuarantine
FROM sys_healthreport hr LEFT JOIN sys_user u ON hr.healthuser=u.id


        AND hr.healthUser = #{filterMap.userid}


        AND hr.reportDate  >=  #{filterMap.begindate}


        AND hr.reportDate  <=  #{filterMap.begindate}


        AND hr.needquarantine = #{filterMap.needquarantine}
